    "events": {
        "onModEnable": function (mod) {
            this.ObjectMaker.getFunction("solid").prototype.nofire = 0;
            this.ObjectMaker.getFunction("solid").prototype.firedeath = 1;
        },
        "onModDisable": function (mod) {
